package com.payment.app.core

import android.content.Context
import android.util.Log
import com.payment.app.data.local.db.DatabaseService
import com.payment.app.data.remote.BankService
import com.payment.app.utils.CommonUtils
import com.payment.app.utils.CommonUtils.TAG
import javax.inject.Inject
import javax.inject.Singleton


@Singleton
class ParameterProcess @Inject constructor(
    private val context: Context,
    private val databaseService: DatabaseService,
    private val state: State,
    private val keyProcess: KeyProcess,
    private val printProcess: PrintProcess,
    private val bankService: BankService
) : BaseProcess(context, databaseService, state){

    fun downloadParameters() {
        Log.d(CommonUtils.TAG, "downloadParameters")

        if (databaseService.getAllTransactions().size > 0) {
            mainViewModel.showMessage("Settlement Needed", 2000)
            return;
        }

        databaseService.prmConst.version = context.packageManager.getPackageInfo(context.packageName, 0).versionCode.toString()
        databaseService.prmConst.model = "PS1"
        databaseService.prmConst.serial = "123456789012"
        databaseService.prmConst.BatchNo = 1
        databaseService.prmConst.TranNo = 0

        databaseService.saveObject(databaseService.prmConst)

        databaseService.prmComm.hostIp = "127.0.0.1"
        databaseService.prmComm.hostPort = 13131

        databaseService.saveObject(databaseService.prmComm)

        databaseService.prmConst.PrmStatus = false
        databaseService.saveObject(databaseService.prmConst)

        if(keyProcess.processKeyExchange()){
            if(processDownloadPrms()){
                databaseService.prmConst.PrmStatus = true;
                databaseService.saveObject(databaseService.prmConst)

                mainViewModel.showMessage("Succeeded", 2000)
            }
        }

        databaseService.saveObject(databaseService.prmConst)
        printProcess.printAppPrms(databaseService.prmConst.PrmStatus)

        mainViewModel.hideMessage(1000)
    }

    fun processDownloadPrms(): Boolean {
        databaseService.prmEmv.index = 0
        databaseService.prmEmv.cakeys = ""
        databaseService.prmEmv.contact = ""
        databaseService.prmEmv.ctlssVisa = ""
        databaseService.prmEmv.ctlssMc = ""

        databaseService.saveObject(databaseService.prmEmv)

        databaseService.prmAcq.index = 0
        databaseService.prmAcq.AcqId = "0001"
        databaseService.prmAcq.AcqName = "Test Bank"
        databaseService.prmAcq.TermId = "10004218"
        databaseService.prmAcq.MercId = "000000000000002"
        databaseService.prmAcq.MercSlipName = "Test Merchant"
        databaseService.prmAcq.MercSlipAddr = "Sariyer/Istanbul"
        databaseService.prmAcq.MercCity = "Istanbul"
        databaseService.prmAcq.DefCurCode = "840"

        databaseService.saveObject(databaseService.prmAcq)

        return true
    }
}